public static boolean hasAllDirectory(File zip, String[] includePaths) {
    Vector<File> dirVec = new Vector<File>();
    File file = new File(zip.getAbsolutePath());
    if (file.isDirectory()) {
        for (String s : includePaths) {
            if (file.list(s+"*").length!= 0)
                dirVec.addElement(file);
