function get(arr, id) {
    return arr.indexOf(id);
}
function myObject(idx) {    //       ↑----------------
    this.id = idx;
    this.value = +arr[idx] // the rest of your object's code
}

myObject.prototype.getId = function() {
    return this.id;
};

