var app = angular.module('docs', []);

  app.controller('ExampleController', function($scope) {
    $scope.type = 1;
    $scope.data = [{"type" : 1, "status" : "A"}, 
                  {"status" : "C","type" : 1}, 
                  {"type" : 1, "status" : "A", "type" : 1}, 
                  {"type" : 2, "status" : "A", "status" : 2}];
  });
   $scope.statusFilter = function(statusItem){
    return statusItem.status == 'A';
    };

